var fs = require('browserify-fs');

export default function LetterConverJson(user: string, title: string, contents: string) {
    const directory = "../TempLetter/"
    var Data = {
        User: user,
        Title: title,
        Contents: contents
    }
    const getTitle = (user: string) => {
        const d = new Date();
        const yyyy = d.getFullYear();
        const mm = d.getMonth() + 1;
        const dd = d.getDate();
        const hh = d.getHours();
        const m = d.getMinutes();
        const s = d.getSeconds()
        return `${yyyy}.${mm}.${dd}/${hh}.${m}.${s}`;
    };

    fs.mkdir('/home', function () {
        fs.writeFile('/home/hello-world.txt', 'Hello world!\n', function () {
            fs.readFile('/home/hello-world.txt', 'utf-8', function (err: any, data: any) {
                console.log(data);
            });
        });
    });
}
