import * as thecamp from 'the-camp-lib';
const dotenv = require('dotenv');

async function LetterApi(user: string, title: string, contents: string) {
    dotenv.config();

    const id = process.env.USER_ID || '';
    const password = process.env.USER_PWD || '';

    const name = process.env.TRAINEE_NAME || '';
    const birth = process.env.TRAINEE_BIRTH || '';
    const enterDate = process.env.ENTER_DATE || '';
    const className = process.env.CLASS_NAME || '';
    const groupName = process.env.GROUP_NAME || '';
    const unitName = process.env.UNIT_NAME || '';

    const getTitle = (user: string, title: string) => {
        const d = new Date();
        const yyyy = d.getFullYear();
        const mm = d.getMonth() + 1;
        const dd = d.getDate();
        return `${yyyy}.${mm}.${dd}일자 ${title} News 모음`;
    };

    const soldier = new thecamp.Soldier(
        name,
        birth,
        enterDate,
        <thecamp.SoldierClassName>className,
        <thecamp.SoldierGroupName>groupName,
        <thecamp.SoldierUnitName>unitName,
        thecamp.SoldierRelationship.FRIEND,
    );

    const client = new thecamp.Client();
    await client.login(id, password);
    await client.addSoldier(soldier);

    const [trainee] = await client.fetchSoldiers(soldier);
    const message = new thecamp.Message(getTitle(user, title), contents, trainee);

    await client.sendMessage(soldier, message);
}

export default LetterApi;
