import React, { ReactElement } from 'react'
import { Button, Input } from "antd";
import { SendOutlined, UserOutlined } from '@ant-design/icons';
import './LetterForm.css'
const { TextArea } = Input;

interface Props {

}

export default function LetterForm({ }: Props): ReactElement {
    return (
        <div>
            <h2>편지 쓰기</h2>
            <p>더캠프 앱을 설치 하지 않아도 아래에 내용을 입력하고 편지 보내기 버튼을 누르면 편지가 전송됩니다.</p>
            <form className='form-container'>
                <label>보내는 사람</label>
                <Input placeholder="보내는 사람의 이름을 입력해주세요." prefix={<UserOutlined />} />
                <label>제목</label>
                <Input placeholder="제목을 입력해주세요." allowClear  />
                <br />
                <label>내용</label>
                <TextArea placeholder="동영이한테 보낼 편지 내용을 입력해주세요." showCount maxLength={1999} allowClear  />
                <br />
                <Button icon={<SendOutlined />} type="primary" shape="round" htmlType="submit">
                    편지 보내기
                </Button>
            </form >
        </div>
    )
}
